-- crear una base de datos llamada empresa
-- gestion de los empleados de una empresa, en la cual distribuida
-- por depas(informatica,publicidad,marketing,finanzas)
-- ademas se requiere controlar al personal(nombre,apellido,id)
-- cada personal puede pertenecer a un departamento
-- nota no todos los empleados tienen departamentos

DROP DATABASE EMPRESA;
CREATE DATABASE empresa;
USE empresa; 

drop table departamentos;
drop table personales;

CREATE TABLE departamentos(
id int primary key not null auto_increment,
nombre varchar(30)
);
-- un personal a la vez puede ser superior de otro personal
CREATE TABLE personales(
id int not null primary key auto_increment,
identificador text,
nombre varchar(30),
apellido varchar(30),
departamento_id int,
supervisor_id int,
foreign key (departamento_id) references departamentos(id),
foreign key (supervisor_id) references personales(id)
);

